#!/bin/sh
# Name: Install Alpine
# Author: GC777
# Icon: data:image/jpeg;base64,
if [ -f /mnt/base-us/alpine.ext3 ]; then
    echo "Existing Alpine install detected, cleaning up..."

    mntroot rw
    rm /mnt/base-us/alpine.ext3
    rm /mnt/base-us/alpine.sh
    rm /mnt/base-us/alpine.log
    rm /mnt/base-us/alpine.conf
    rm /mnt/base-us/alpine.zip
    rm /etc/upstart/alpine.conf
    mntroot ro

    echo "Cleanup complete."
fi
cd /mnt/base-us/
mntroot rw
ALPINE_URL="https://github.com/schuhumi/alpine_kindle/releases/download/v0.2-alpha2/alpine.zip"
ALPINE_SC_URL="https://github.com/GreenCat-777/quicklinux-misc-files/releases/download/qai/alpinesc.sh"
ALPINE_UNINSTALL_URL="https://github.com/GreenCat-777/quicklinux-misc-files/releases/download/qai/uninstall_alpine.sh"
echo "Download starting, this may take a minute"
curl -L -o "alpine.zip" "$ALPINE_URL"
unzip alpine.zip
rm alpine.zip
cp /mnt/base-us/alpine.conf /etc/upstart/alpine.conf
mntroot ro
cd /mnt/us/documents/
curl -L -o "alpinesc.sh" "$ALPINE_SC_URL"
curl -L -o "uninstall_alpine.sh" "$ALPINE_UNINSTALL_URL"
rm /mnt/us/documents/install_alpine.sh